// src/components/HomePage.js
import React from 'react';
import { Container, Typography, Box } from '@mui/material';
import '../styles/HomePage.css';
import spaImage from '../images/spa.jpg';
import maniPediImage from '../images/manicure.jpg';
import waxingImage from '../images/waxing.jpg';

const items = [
  {
    name: "Spa",
    description: "Relax and rejuvenate with our spa treatments.",
    image: spaImage
  },
  {
    name: "Mani/Pedi",
    description: "Perfect nails with our mani/pedi services.",
    image: maniPediImage
  },
  {
    name: "Waxing",
    description: "Smooth and silky skin with our waxing services.",
    image: waxingImage
  }
];

const HomePage = () => {
  return (
    <Container className="container">
      <section className="hero-section">
        <p className="hero-text">
          Nothing improves your mood like a new manicure.
        </p>
      </section>
      <Box mt={4}>
        <Carousel>
          {items.map((item, index) => (
            <Box key={index} className="carousel-item">
              <img src={item.image} alt={item.name} className="carousel-image" />
              <Typography variant="h6">{item.name}</Typography>
              <Typography>{item.description}</Typography>
            </Box>
          ))}
        </Carousel>
      </Box>
    </Container>
  );
};

export default HomePage;
